import logo from './logo.svg';
import './App.css';
import { Link, Element } from 'react-scroll';

import Landing from './components/Landing'
import Works from './components/Works'
import About from './components/About'

import { Component, useRef } from 'react';

function App() {

  const worksRef = useRef(HTMLDivElement | null);

  const handleClick = () => {
    if (true) {
      worksRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }

  return (
    <div className="App">
      <ul className="nav">
        {/* Links to scroll to respective sections */}
        <li><Link to="landing" smooth={true} duration={500}>Landing</Link></li>
        <li><Link to="works" smooth={true} duration={500}>Works</Link></li>
        <li><Link to="about" smooth={true} duration={500}>About</Link></li>
      </ul>

      {/* Scrollable sections */}
      <Element name="landing" className="section"><Landing /></Element>
      <Element name="works" className="section"><Works /></Element>
      <Element name="about" className="section"><About /></Element>
    </div>
  );
}

export default App;
