import logo from './logo.svg';
import './App.css';
import { Link, Element } from 'react-scroll';

import Landing from './components/Landing'
import Works from './components/Works'
import About from './components/About'

import { Component, useRef } from 'react';

function App() {

  /*const worksRef = useRef(HTMLDivElement | null);

  const handleClick = () => {
    if (true) {
      worksRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }*/

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY || window.pageYOffset;
      const landingOffset = landingRef.current.offsetTop;
      const worksOffset = worksRef.current.offsetTop;
      const aboutOffset = aboutRef.current.offsetTop;

      if (scrollPosition < worksOffset) {
        window.scrollTo({ top: landingOffset, behavior: 'smooth' });
      } else if (scrollPosition >= worksOffset && scrollPosition < aboutOffset) {
        window.scrollTo({ top: worksOffset, behavior: 'smooth' });
      } else {
        window.scrollTo({ top: aboutOffset, behavior: 'smooth' });
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <>
      <Landing ref={landingRef}></Landing>
      <Works ref={worksRef}></Works>
      <About></About>
    </>
  );
}

export default App;
