import React, { useRef, useEffect } from 'react';
import './App.css';

function App() {
  const landingRef = useRef(null);
  const worksRef = useRef(null);
  const aboutRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY || window.pageYOffset;
      const landingOffset = landingRef.current.offsetTop;
      const worksOffset = worksRef.current.offsetTop;
      const aboutOffset = aboutRef.current.offsetTop;
      const threshold = 100; // Adjust this value according to your preference

      if (scrollPosition < worksOffset - threshold) {
        window.scrollTo({ top: landingOffset, behavior: 'smooth' });
      } else if (scrollPosition >= worksOffset - threshold && scrollPosition < aboutOffset - threshold) {
        window.scrollTo({ top: worksOffset, behavior: 'smooth' });
      } else {
        window.scrollTo({ top: aboutOffset, behavior: 'smooth' });
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <>
      <div className='landing-container' ref={landingRef}>
        <h1>Landing</h1>
      </div>
      <div className='works-container' ref={worksRef}>
        <h1>Works</h1>
      </div>
      <div className='about-container' ref={aboutRef}>
        <h1>About</h1>
      </div>
    </>
  );
}

export default App;
