import React, { useRef, useEffect } from 'react';
import './App.css';
import Landing from './components/Landing';
import Works from './components/Works';
import About from './components/About';

function App() {
  const worksRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      // Detect the scroll position
      const scrollY = window.scrollY || window.pageYOffset;

      // Calculate the offset of the Works component from the top of the page
      const worksOffsetTop = worksRef.current.offsetTop;

      // If scrolled to or past the Works component, smoothly scroll to it
      if (scrollY >= worksOffsetTop) {
        worksRef.current.scrollIntoView({ behavior: 'smooth' });
      }
    };

    // Add scroll event listener when the component mounts
    window.addEventListener('scroll', handleScroll);

    // Clean up by removing the event listener when the component unmounts
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []); // Empty dependency array to run effect only once on mount

  return (
    <>
      <Landing />
      <Works ref={worksRef} />
      <About />
    </>
  );
}

export default App;
