import logo from './logo.svg';
import './App.css';
import { useRef, useEffect } from 'react';

function App() {
  const landingRef = useRef(null);
  const worksRef = useRef(null);
  const aboutRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY || window.pageYOffset;
      const landingTop = landingRef.current.getBoundingClientRect().top;
      const worksTop = worksRef.current.getBoundingClientRect().top;
      const aboutTop = aboutRef.current.getBoundingClientRect().top;

      if (scrollPosition < worksTop / 2) {
        landingRef.current.scrollIntoView({ behavior: 'smooth' });
      } else if (scrollPosition >= worksTop / 2 && scrollPosition < aboutTop / 2) {
        worksRef.current.scrollIntoView({ behavior: 'smooth' });
      } else {
        aboutRef.current.scrollIntoView({ behavior: 'smooth' });
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <>
      <div className='landing-container' ref={landingRef}>
        <h1>Lhanding</h1>
      </div>
      <div ref={worksRef} className='works-container'>
        <h1>Works</h1>
      </div>
      <div ref={aboutRef} className='about-container'>
        <h1>About</h1>
      </div>
    </>
  );
}

export default App;
